import { makeStyles } from "@mui/styles"



export const useStyles = makeStyles({

  firstSection:{
    marginTop:'60px !important',
    maxWidth:'calc(1920px * 0.95) !important'
  },
  firstColums:{
    textAlign:'left',
    padding:'30px',
    '& p':{
      fontSize:'55px',
    },
    '& p:last-child':{
      fontSize:'20px',
    }

  }
})
