import { makeStyles } from "@mui/styles";


export const useStyles = makeStyles({
  cards:{
    display:'flex',
    padding:"0 32px",
  },
  topic:{
    '& p':{
      textAlign:'start',
      marginLeft:44,
      letterSpasing:1.5,
      fontSize:40
    }
  },
  firstRow:{
    display:"flex"
  },
  secondRow:{
    display:'flex'
  },
  '@media(max-width:1200px)':{
    cards:{
      flexDirection:'column'
    }
  }
})
