import { makeStyles } from "@mui/styles"



export const useStyles = makeStyles({

  firstSection:{
    marginTop:'120px !important',
    maxWidth:'calc(1920px * 0.9) !important'
  },
  firstColums:{
    textAlign:'left',
    '& p':{
      fontSize:'55px',
    },
    '& p:nth-child(3)':{
      fontSize:'20px',
      marginTop:'47px',
    }

  },
  secondColums:{
    '& img':{
      display:'block',
      float:'right',
    }
  },
  '@media(max-width:1250px)':{
    firstColums:{
      '& p':{
        fontSize:45,
      }
    }
  },'@media(max-width:9000px)':{
    secondColums:{
      display:'none'
    }
  }
})
