import { makeStyles } from "@mui/styles";


export const useStyles = makeStyles({
  phones:{
    display:'flex',
    justifyContent:'center',
  },
  topic:{
    textTransform:'uppercase',
    fontSize:'16px !important',
    fontWeight:'700 !important',
    textAlign:'start',
    letterSpacing:'2px',
  },
  container:{
    width:'686px',
    margin:"0 auto",
    marginTop:'96px'
  },
  '@media(max-width:800px)':{
    flexDirection:'column'
  }
})
