import { makeStyles } from "@mui/styles"


export const useStyles = makeStyles({
  logo:{
    color:"black",
    fontWeight:"bold !important"
  },
  headerBar:{
    textAlign:'left',
    backgroundColor:'white !important',
    boxShadow:'none !important',
  },
  itemMenu:{
    color:'black',
    marginLeft:24,
    "&:first-child": {
      marginLeft: 0
    }
  },
  menu:{
    display:'flex',
    marginLeft:'20px',
    alignItems:'self-start',

  },
  '@media(max-width:600px)':{
    menu:{
      display:'none'
    },
    itemMenu:{
      marginRight:24,
      marginTop:10,
      "&:first-child": {
        marginLeft: 24,
        marginTop:0
      }
    }
  }
})
