import { makeStyles } from "@mui/styles";


export const useStyles = makeStyles({
  container:{
    display:"flex"
  },
  text:{
    '& p':{
      letterSpasing:1.8,
      fontSize:55,
      width:'100%',
      textAlign:'start',
      margin:'0 110px'
    }
  },
  secondColumn:{
    paddingRight:112,
    width:'50%',
    '& img':{
      float:'right'
    }
  },
  getStarted:{
    marginLeft:110,
    marginTop:61
  },
  '@media(max-width:1200px)':{
    secondColumn:{
      display:'none'
    },
    text:{
      '& p':{
        fontSize:45,
      }
    }
  }
})
