import { makeStyles } from "@mui/styles";


export const useStyles = makeStyles({
  container:{
    width:"100%",
    marginTop:200
  },
  getStarted:{
    margin:'0 auto'
  },
  text:{
    '& p':{
      letterSpacing:2.25,
      textAlign:'center',
      fontSize:64,
      fontWeight:'bold',
    }
  },
  '@media(max-width:1200px)':{
    text:{
      '& p':{
        fontSize:44,
      }
    }
  },
  '@media(max-width:500px)':{
    text:{
      '& p':{
        fontSize:34,
      }
    }
  }
})
